sp_widget.client_script:
function($rootScope,$scope) {
	/* widget controller */
	var c = this;
	c.selectItem = function(idx) {
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);

		$rootScope.$on('update', function (event, data) {
			c.data.notes.map(function(item) {
				if (item.sys_id === $rootScope.updatedNote.sys_id) {
					item.title =  $rootScope.updatedNote.title;
					item.note =  $rootScope.updatedNote.note;
				}
			})
		})
	}
}

sp_widget.script:
(function() {
	// table: 'x_snc_createnotes_note'
	// data:{notes:[{}]}
	// db query for all record of login user (gs.getUser().getID())
	// sort record in des order by sys_created_on date field ('sys_created_on') value
	// Each record:
	// empty object noteObj(number, title, sys_id) - $sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id');
	// first 20 character in des field -> noteObj
	// push noteObj into notes array
	data.notes = [];
	var queryRecord = new GlideRecord('x_snc_createnotes_note');
	queryRecord.addQuery('user', gs.getUser().getID());
	queryRecord.orderByDesc('sys_created_on');
	queryRecord.query();

	while(queryRecord.next()) {
		var noteObj = {};
		$sp.getRecordDisplayValues(noteObj, queryRecord, 'number,title,sys_id');
		noteObj.note = queryRecord.getValue('note').slice(0, 20);
		data.notes.push(noteObj);
	}
})();
